import React, { useState } from "react";
import {
  AppBar,
  Button,
  Container,
  Drawer,
  IconButton,
  List,
  ListItemButton,
  ListItemText,
  Tab,
  Tabs,
  Toolbar,
  Typography,
  useMediaQuery,
  useTheme,
  Badge
} from "@mui/material";
import AddBusinessRoundedIcon from "@mui/icons-material/AddBusinessRounded";
import MenuIcon from "@mui/icons-material/Menu";
import { NavLink } from "react-router-dom";


const Navbar = () => {
  const [value, setValue] = useState();
  const [openDrawer, setOpenDrawer] = useState(false);
  const theme = useTheme();
  const isMatch = useMediaQuery(theme.breakpoints.down("md"));

  return (
    <React.Fragment>
      <AppBar sx={{ background: "#f5f5fd", color: "black", boxShadow: "none", height: '70px' }}>
        <Container maxWidth="xl">
          <Toolbar>
            {/* ... (previous code) */}
            {isMatch ? (
              <>
                {/* ... (previous code) */}
                <Drawer
                  anchor="left"
                  open={openDrawer}
                  onClose={() => setOpenDrawer(false)}
                >
                  <List sx={{ width: '200px', textDecoration: 'none' }}>
                    {/* ... (previous code) */}
                  </List>
                </Drawer>
              </>
            ) : (
              <>
                <Tabs
                  sx={{ marginLeft: "auto" }}
                  indicatorColor="secondary"
                  textColor="inherit"
                  value={value}
                  onChange={(e, value) => setValue(value)}
                >
                  <NavLink to='/' activeClassName="active"><Tab sx={{ "&:hover": { fontWeight: 'bold' } }} label="Home" /></NavLink>
                  <NavLink to='/products' activeClassName="active"><Tab sx={{ "&:hover": { fontWeight: 'bold' } }} label="Products" /></NavLink>
                  <NavLink to='/cart' activeClassName="active"><Tab sx={{ "&:hover": { fontWeight: 'bold' } }} label="Cart" /></NavLink>
                  <NavLink to='/about' activeClassName="active"><Tab sx={{ "&:hover": { fontWeight: 'bold' } }} label="About" /></NavLink>
                  <NavLink to='/contact' activeClassName="active"><Tab sx={{ "&:hover": { fontWeight: 'bold' } }} label="Contact" /></NavLink>
                </Tabs>
                <Button
                  sx={{
                    marginLeft: "auto",
                    backgroundColor: "#6b3bc4",
                    color: "white",
                    "&:hover": { backgroundColor: "#5931a3" },
                  }}
                  variant="contained"
                >
                  Login
                </Button>
              </>
            )}
          </Toolbar>
        </Container>
      </AppBar>
    </React.Fragment>
  );
};

export default Navbar;
