import React,{useEffect} from 'react';
import '../styles/SingleProduct.css'
import Container from '@mui/material/Container';
import { useParams } from 'react-router-dom';
import ProductItems from '../context/products.json';
import { useProductContext } from '../context/ProductContext';
import { colors } from '@mui/material';

export default function SingleProduct() {
  const {getSingleProduct, isSingleLoading, singleProduct} = useProductContext();
  const {id} = useParams();

  const {id:alias,name,image,company,price,description,category,stock,stars,reviews} = singleProduct;

  useEffect(() => {const selectedProduct = ProductItems.find(product => product.id === id);
    if (selectedProduct) {getSingleProduct(selectedProduct);
    } else {console.error(`Product with ID ${id} not found.`);
    }
  }, []);

  console.log(singleProduct);
  return (
    <Container>
      <div className='singleProduct'>
        <div className='container'>
          <div class='sp-head'>
            <p>Product \ {name}</p>
          </div>
          <div className='sp-body'>
            <div className="product-img">
              <img src={image} alt='product' height="300px" width="400px" />
            </div>
            <div className='product-details'>
              <div className='pd-head'>
                <h3>{name}</h3>
                <h6>{price}</h6>
                <p>{reviews}</p>
              </div>
              <div className='pd-body'>
                <p>Name: {name}</p>
                <p>Category: {category}</p>
                <p>Company: {company}</p>
                <p>Description: {description}</p>
                <p>Stock: {stock}</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </Container>
  )
}
