import { createContext, useContext, useEffect, useReducer } from "react";
import reducer from '../reducer/CartReducer';

const CartContext = createContext();

const getLocalCartData = () =>{
    let localCartData = localStorage.getItem("pkCart");
    if(localCartData === []) return [];
    else return JSON.parse(localCartData);
}

const initialState = {
    cart: getLocalCartData(),
    total_items:"",
    total_amount:"",
    shipping_fee: 5000,
}

const calculateTotalCost = () => {
    const totalCost = state.cart.reduce((acc, item) => {
      return acc + item.amount * item.price;
    }, 0);

    return totalCost;
  };

const CartProvider = ({children}) => {

    const[state,dispatch] = useReducer(reducer, initialState)

    const addToCart = (amount,product) => {
        dispatch({type:"ADD_TO_CART", payload:{amount,product}})
    }

    const removeItem = (id)=>{
        dispatch({type:"REMOVE_ITEM",payload:id})
    }

    useEffect(()=>{
        localStorage.setItem("pkCart",JSON.stringify(state.cart))
    },[state.cart])

    return <CartContext.Provider value={{...state,addToCart,removeItem,calculateTotalCost}}>{children}</CartContext.Provider>
};


const useCartContext = ()=>{
    return useContext(CartContext);
}

export {CartProvider, useCartContext }
