const ProductReducer = (state, action)=>{
    if(action.type === "SET_LOADING"){
        return {
            ...state,
            isLoading: true,
        };
    }
    if(action.type === "JSON_ERROR"){
        return{
            ...state,
            isLoading:false,
            isError: true
        }
    }
    switch (action.type){
        case "SET_LOADING":
            break;
        default:
            return{
                ...state,
                isLoading:false,
                isError: true
            }
    }
    return state;
}
