import React,{useState} from 'react';
import { useCartContext } from '../context/CartContext';
import { NavLink } from 'react-router-dom';
import CartAmountToggle from '../components/CartAmountToggle';


export default function CartItem({id,name,image,company,price,amount,stock}) {

    const [quantity,setQuantity] = useState(amount);


    const {removeItem,setDecrease, setIncrement} = useCartContext();

  return (
    <div className='cart-item'>
        <div className='cart-img'>
            <img src={image} alt='product' height="120px" width="170px" />
            <div className='quantity'>
            <CartAmountToggle
            amount={amount}
            setDecrease={() => setDecrease(id)}
            setIncrease={() => setIncrement(id)}
            />
            </div>
        </div>
        <div className='cart-details'>
            <h4>{name}</h4>
            <p>{company}</p>
            <h3>{new Intl.NumberFormat('en-IN', { style: 'currency', currency: 'INR',maximumSignificantDigits: 3 }).format(
    price,
  )}</h3>
            <NavLink to='/'><button id="buyNow">Buy Now</button></NavLink>
            <button onClick={()=> removeItem(id)} id="remove">Remove</button>
        </div>
        <div className='cart-total'>
            <table>
                <tr>
                    <th>Total Count of Items:</th>
                    <td><p>{quantity}</p></td>
                </tr><br/>
                <tr>
                    <th>Shipping cost:</th>
                    <td><p>Free</p></td>
                </tr><br/>
                <tr>
                    <th>Total net amount:</th>
                    <td><p>{new Intl.NumberFormat('en-IN', { style: 'currency', currency: 'INR',maximumSignificantDigits: 3 }).format(
    quantity * price,
  )}</p></td>
                </tr>
            </table>
        </div>
    </div>
  )
}
