import React,{useState} from 'react'

export default function CartItem({id,name,image,company,price,amount,stock}) {

    const [quantity,setQuantity] = useState(amount);

    const setDecrease = (quantity)=>{
        quantity>1?setQuantity(quantity-1):setQuantity(1);
    }

    const setIncrease = (quantity)=>{
        quantity>=stock?setQuantity(stock):setQuantity(quantity+1);
    }

  return (
    <div className='cart-item'>
        <div className='cart-img'>
            <img src={image} alt='product' height="120px" width="170px" />
            <div className='quantity'>
            <span onClick={setDecrease}>-</span><span>{quantity}</span><span onClick={setIncrease}>+</span>
            </div>
        </div>
        <div className='cart-details'>
            <h4>{name}</h4>
            <p>{company}</p>
            <h3>{new Intl.NumberFormat('en-IN', { style: 'currency', currency: 'INR', maximumSignificantDigits: 3 }).format(
    price,
  )}</h3>
            <button>Remove</button>
        </div>
    </div>
  )
}
