import React from 'react'
import { useFilterContext } from '../context/FilterContext'

export default function FilterSection() {
    const {
        filters: {text},
        all_products,
        updateFilterValue,
    } = useFilterContext();

    const getUniqueData = (data,prop)=>{
      let newVal = data.map((currEle)=>{
        return currEle[prop]
      })
      newVal = ["All",...new Set(newVal)]
      console.log("hii:",newVal)
      return newVal;
    };

    const categoryOnlyData = getUniqueData(all_products,"category")

  return (
    <div>
        <form onSubmit={(e)=> e.preventDefault()}>
              <input type='text' name='text' placeholder='Search your product' value={text} id='input' onChange={updateFilterValue}/>
              <input type='submit' name='submit' value='Search' id='submit'/>
        </form>
        <div className='category-filter'>
        {categoryOnlyData.map((currEle, index) => {
        const buttonStyle = {
          backgroundColor: selectedCategory === currEle ? 'blue' : 'gray', // Change 'blue' to your desired color
          color: selectedCategory === currEle ? 'white' : 'black', // Change 'white' to your desired text color
        };
        return (
          <button
            key={index}
            type="button"
            name="category"
            value={currEle}
            onClick={updateFilterValue}
            style={buttonStyle}
          >
            {currEle}
          </button>
        );
      })}
        </div>
        <div className="filter-clear">
          <button> Clear Filter</button>
        </div>
    </div>
  )
}
