export default function CartReducer(state,action) {
  if(action.type === "ADD_TO_CART"){
    let {amount,product} = action.payload;

    let cartProduct = {
      id: product.id,
      name: product.name,
      company:product.company,
      price: product.price,
      image: product.image,
      amount,
      stock: product.stock,
    };

    return {
      ...state,
      cart:[...state.cart,cartProduct],
    }
  }
  if(action.type==="REMOVE_ITEM"){
    let updatedCart = state.cart.filter((currEle)=> currEle.id !== action.payload)
    return{
      ...state,
      cart:updatedCart,

    }
  }
  return state;
}
