const ProductReducer = (state, action)=>{
    switch (action.type){
        case "SET_LOADING":
            return {
                ...state,
                isLoading: true,
            };
        case "JSON_ERROR":
            return{
                ...state,
                isLoading:false,
                isError: true
            }
        default:
            return state
    }
}
