export default function CartReducer(state,action) {
  if(action.type === "ADD_TO_CART"){
    let {amount,product} = action.payload;

    let cartProduct;

    cartProduct = {
      id: product.id,
      name: product.name,
      price: product.price,
      image: product.image,
      amount:amount,
      stock: product.stock
    }

    return {
      ...state,
      cart:[...state.cart,cartProduct],

    }
  }
  return state;
}
