import React, { useState } from "react";
import {
  AppBar,
  Button,
  Container,
  Tab,
  Tabs,
  Toolbar,
  Typography,
  useMediaQuery,
  useTheme,
} from "@mui/material";
import AddBusinessRoundedIcon from "@mui/icons-material/AddBusinessRounded";
import DrawerComp from "./Drawer";
const Header = () => {
  const [value, setValue] = useState();
  const theme = useTheme();
  console.log(theme);
  const isMatch = useMediaQuery(theme.breakpoints.down("md"));
  console.log(isMatch);

  return (
    <React.Fragment>
      <AppBar sx={{ background: "#f5f5fd",color:'black',boxShadow:'none'}}>
        <Container maxWidth='xl'>
        <Toolbar>
          <AddBusinessRoundedIcon sx={{ transform: "scale(2)",color:'#6b3bc4'}} />
          <Typography sx={{ color:'#6b3bc4',fontSize: "2rem", paddingLeft: "2%",display: { xs: 'none', md: 'flex' }}}>
                Payakon
          </Typography>
          {isMatch ? (
            <>
              <Typography sx={{ color:'#6b3bc4',fontSize: "2rem", paddingLeft: "3%" }}>
                Payakon
              </Typography>
              <DrawerComp />
            </>
          ) : (
            <>
              <Tabs
                sx={{ marginLeft: "auto" }}
                indicatorColor="secondary"
                textColor="inherit"
                value={value}
                onChange={(e, value) => setValue(value)}
              >
                <Tab label="Products" />
                <Tab label="Services" />
                <Tab label="About" />
                <Tab label="Contact" />
              </Tabs>
              <Button sx={{ marginLeft: "auto", backgroundColor:'#6b3bc4', color:'white' }} variant="contained">
                Login
              </Button>
              <Button sx={{ marginLeft: "10px", backgroundColor:'#6b3bc4', color:'white'}} variant="contained">
                SignUp
              </Button>
            </>
          )}
        </Toolbar>
        </Container>
      </AppBar>
    </React.Fragment>
  );
};

export default Header;
