import React, { useState } from "react";
import {
  AppBar,
  Button,
  Container,
  Drawer,
  IconButton,
  List,
  ListItemButton,
  ListItemText,
  Tab,
  Tabs,
  Toolbar,
  Typography,
  useMediaQuery,
  useTheme,
} from "@mui/material";
import AddBusinessRoundedIcon from "@mui/icons-material/AddBusinessRounded";
import MenuIcon from "@mui/icons-material/Menu";

const Navbar = () => {
  const [value, setValue] = useState();
  const [openDrawer, setOpenDrawer] = useState(false);
  const theme = useTheme();
  const isMatch = useMediaQuery(theme.breakpoints.down("md"));

  return (
    <React.Fragment>
      <AppBar sx={{ background: "#f5f5fd", color: "black", boxShadow: "none" }}>
        <Container maxWidth="xl">
          <Toolbar>
            <AddBusinessRoundedIcon
              sx={{ transform: "scale(2)", color: "#6b3bc4" }}
            />
            <Typography
              sx={{
                color: "#6b3bc4",
                fontSize: "2rem",
                paddingLeft: "2%",
                display: { xs: "none", md: "flex" },
              }}
            >
              Payakon
            </Typography>
            {isMatch ? (
              <>
                <Typography
                  sx={{ color: "#6b3bc4", fontSize: "2rem", paddingLeft: "3%" }}
                >
                  Payakon
                </Typography>
                <IconButton
                  sx={{ color: "black", marginLeft: "auto" }}
                  onClick={() => setOpenDrawer(!openDrawer)}
                >
                  <MenuIcon color="black" />
                </IconButton>
                <Drawer
                  anchor="left"
                  open={openDrawer}
                  onClose={() => setOpenDrawer(false)}

                >
                  <List sx={{width:'200px'}}>
                    <ListItemButton sx={{"&:hover":{color:'#6b3bc4',backgroundColor:'#f5f5fd'}}}>
                      <ListItemText>Products</ListItemText>
                    </ListItemButton>
                    <ListItemButton>
                      <ListItemText>Cart</ListItemText>
                    </ListItemButton>
                    <ListItemButton>
                      <ListItemText>About</ListItemText>
                    </ListItemButton>
                    <ListItemButton>
                      <ListItemText>Contact</ListItemText>
                    </ListItemButton>
                    {/* Add similar ListItemButton components for other items */}
                  </List>
                </Drawer>
              </>
            ) : (
              <>
                <Tabs
                  sx={{ marginLeft: "auto" }}
                  indicatorColor="secondary"
                  textColor="inherit"
                  value={value}
                  onChange={(e, value) => setValue(value)}
                >
                  <Tab label="Products" />
                  <Tab label="Cart" />
                  <Tab label="About" />
                  <Tab label="Contact" />
                  {/* Add similar Tab components for other items */}
                </Tabs>
                <Button
                  sx={{
                    marginLeft: "auto",
                    backgroundColor: "#6b3bc4",
                    color: "white",
                    "&:hover": { backgroundColor: "#5931a3" },
                  }}
                  variant="contained"
                >
                  Login
                </Button>
                <Button
                  sx={{
                    marginLeft: "10px",
                    backgroundColor: "#6b3bc4",
                    color: "white",
                    "&:hover": { backgroundColor: "#5931a3" },
                  }}
                  variant="contained"
                >
                  SignUp
                </Button>
              </>
            )}
          </Toolbar>
        </Container>
      </AppBar>
    </React.Fragment>
  );
};

export default Navbar;
