import React, { useState } from "react";
import { AppBar, Button, Container, Drawer, IconButton, List, ListItemButton, Tab, Tabs, Toolbar, Typography } from "@mui/material";
import AddBusinessRoundedIcon from "@mui/icons-material/AddBusinessRounded";
import MenuIcon from "@mui/icons-material/Menu";
import { NavLink } from "react-router-dom";
import styled from "styled-components";

const StyledAppBar = styled(AppBar)`
  background: #f5f5fd;
  color: black;
  box-shadow: none;
  height: 70px;
`;

const StyledLogo = styled(AddBusinessRoundedIcon)`
  transform: scale(2);
  color: #6b3bc4;
`;

const StyledTypography = styled(Typography)`
  color: #6b3bc4;
  font-size: 2rem;
  padding-left: 2%;
  display: none;

  @media (min-width: 600px) {
    display: flex;
  }
`;

const StyledTabs = styled(Tabs)`
  margin-left: auto;
`;

const StyledButton = styled(Button)`
  margin-left: auto;
  background-color: #6b3bc4;
  color: white;

  &:hover {
    background-color: #5931a3;
  }
`;

const StyledDrawerList = styled(List)`
  width: 200px;
  text-decoration: none;
`;

const StyledListItemButton = styled(ListItemButton)`
  padding: 10px;

  &:hover {
    color: #6b3bc4;
    background-color: #f5f5fd;
  }
`;

const Navbar = () => {
  const [value, setValue] = useState();
  const [openDrawer, setOpenDrawer] = useState(false);
  const theme = useTheme(); // Define the 'theme' variable here
  const isMatch = useMediaQuery(theme.breakpoints.down("md")); // Define 'isMatch' using 'useMediaQuery'

  const handleDrawerClose = () => {
    setOpenDrawer(false);
  };

  return (
    <React.Fragment>
      <StyledAppBar>
        <Container maxWidth="xl">
          <Toolbar>
            <StyledLogo />
            <StyledTypography>Payakon</StyledTypography>
            {isMatch ? (
              <>
                <StyledTypography>Payakon</StyledTypography>
                <IconButton sx={{ color: "black", marginLeft: "auto" }} onClick={() => setOpenDrawer(!openDrawer)}>
                  <MenuIcon color="black" />
                </IconButton>
                <Drawer anchor="left" open={openDrawer} onClose={handleDrawerClose}>
                  <StyledDrawerList>
                    {navLinks.map((link) => (
                      <StyledListItemButton key={link.to}>
                        <NavLink to={link.to} onClick={handleDrawerClose}>
                          <ListItemText>{link.label}</ListItemText>
                        </NavLink>
                      </StyledListItemButton>
                    ))}
                  </StyledDrawerList>
                </Drawer>
              </>
            ) : (
              <>
                <StyledTabs indicatorColor="secondary" textColor="inherit" value={value} onChange={(e, value) => setValue(value)}>
                  {navLinks.map((link) => (
                    <NavLink to={link.to} key={link.to}>
                      <Tab sx={{"&:hover":{fontWeight:'bold'}}} label={link.label} />
                    </NavLink>
                  ))}
                </StyledTabs>
                <StyledButton variant="contained">Login</StyledButton>
              </>
            )}
          </Toolbar>
        </Container>
      </StyledAppBar>
    </React.Fragment>
  );
};

const navLinks = [
  { to: "/", label: "Home" },
  { to: "/products", label: "Products" },
  { to: "/cart", label: "Cart" },
  { to: "/about", label: "About" },
  { to: "/contact", label: "Contact" },
  // Add more links as needed
];

export default Navbar;
